/* eslint-disable react-hooks/rules-of-hooks */

import React from "react";
import {
  Card,
  CardBody,
  CardFooter,
  Row,
  Col,
} from "reactstrap";
import { useLocation } from "react-router-dom";
import PerfectScrollbar from "perfect-scrollbar";
import Navbar from "components/Navbars/Navbar.js";
import Footer from "components/Footer/Footer.js";
import Sidebar from "components/Sidebar/Sidebar.js";
// import FixedPlugin from "components/FixedPlugin/FixedPlugin.js";

import routes from "routes.js";

var ps;
function ProductDetails(props) {
  const [products, setProducts] = useState([]);
  const { productId } = useParams(); // Extract the productId from the URL params

  const fetchProducts = () => {
    axios.get('http://localhost:3001/user/explore-products/')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  };

  useEffect(() => {
    fetchProducts();
  }, []);

const mainPanel = React.useRef();
  const location = useLocation();
  React.useEffect(() => {
    if (navigator.platform.indexOf("Win") > -1) {
      ps = new PerfectScrollbar(mainPanel.current);
      document.body.classList.toggle("perfect-scrollbar-on");
    }
    return function cleanup() {
      if (navigator.platform.indexOf("Win") > -1) {
        ps.destroy();
        document.body.classList.toggle("perfect-scrollbar-on");
      }
    };
  });
  React.useEffect(() => {
    mainPanel.current.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
  }, [location]);
  // const handleActiveClick = (color) => {
  //   setActiveColor(color);
  // };
  // const handleBgClick = (color) => {
  //   setBackgroundColor(color);
  // };
  return (
    <>
     <div className="wrapper">
      <Sidebar
        {...props}
        routes={routes}
        // bgColor={backgroundColor}
        // activeColor={activeColor}
      />
      <div className="main-panel" ref={mainPanel}>
        <Navbar {...props} />
      <div className="content">
        {productId ? (
          // Render details of the selected product
          <h4 align="center">Product Details for ID: {productId}</h4>
        ) : (
          // Render the list of products
          <Row>
            {products.map(product => (
              <Col lg="4" md="6" sm="6" key={product._id}>
                <Card className="card-stats">
                  <CardBody>
                    <div className="icon-big text-center icon-warning">
                      <i style={{ fontSize: '68px' }} className={product.product_icon} />
                    </div>
                    <div>
                      <h6 align="center" className="prod-name" style={{ textDecoration: 'none !important' }}>{product.product_name}</h6>
                    </div>
                  </CardBody>
                  <CardFooter>
                    <hr />
                    <div style={{ textDecoration: 'none !important' }} className="stats prod-name">
                      {product.product_description}
                    </div>
                  </CardFooter>
                  <CardFooter style={{ textAlign: 'center' }}>
                    {/* Pass the product ID to the details route */}
                    <Link to={`/user/explore-products/details/${product._id}`}>
                      <button className="prod-button">View More</button>
                    </Link>
                  </CardFooter>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </div>
      </div>
      </div>
    </>
  );
}

export default ProductDetails;
