import React, { useState, useEffect, useCallback } from "react";
import { Card, CardBody, Row, Col } from "reactstrap";
import axios from "axios";
import jwt from 'jsonwebtoken';

function Preview() {
  const [userData, setUserData] = useState();
  const [profiles, setProfiles] = useState();


  const fetchProfile = useCallback(() => {
    axios.get(`http://localhost:3001/user/preview/${userData.userId}`, { withCredentials: true })
      .then(response => {
        if (response && response.data) {
          setProfiles(response.data);
        } else {
          console.error('Empty response or invalid data received');
          // Handle the case where response or data is empty or invalid
        }
      })
      .catch(error => {
        console.error('Error fetching profile:', error);
        // Handle API call errors
      });
  }, [userData]);

  useEffect(() => {
    const token = localStorage.getItem('token') || document.cookie.replace(/(?:(?:^|.*;\s*)token\s*=\s*([^;]*).*$)|^.*$/, "$1");
    if (token) {
      const decoded = jwt.decode(token);
      setUserData(decoded);
    }
  }, []);

  useEffect(() => {
    if (userData) {
      fetchProfile();
    }
  }, [userData, fetchProfile]);

  return (
    <div className="content">
      <Row>
        {/* {profiles.map(profile => ( */}
          <Col lg="4" md="6" sm="6" 
          // key={profile._id}
          >
            <Card className="card-stats">
              <CardBody>
                <div className="icon-big text-center icon-warning">
                  <i style={{ fontSize: '68px' }} 
                  // className={profile.firstname} 
                  />
                </div>
                <div>
                  <h6 align="center" className="prod-name" style={{ textDecoration: 'none !important' }}>
                    {/* {profile.lastname} */}
                    </h6>
                </div>
              </CardBody>
            </Card>
          </Col>
        {/* ))} */}
      </Row>
    </div>
  );
}

export default Preview;
