import React, {useState, useEffect} from "react";
// react plugin used to create charts
// reactstrap components
import {
  Card,
  CardBody,
  CardFooter,
  CardTitle,
  Row,
  Col,
} from "reactstrap";
import '../assets/css/style.css';
import { Link } from "react-router-dom";  
import axios from "axios";

function Products() {
  const [products, setProducts] = useState([]);
  const fetchProducts = () => {
    axios.get('http://localhost:3001/admin/products/')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  };
  
  useEffect(() => {
    fetchProducts();
  }, []);
  return (
    <>
      <div className="content">
        <Row>
        <Col lg="3" md="6" sm="6" 
        style= {{margin:'0em auto 2em 0em'}}
        >
         <Link to='add-product/'> <button className="add-product">Add A Product</button></Link>
        </Col>
        </Row>
        {/* {products.map(product => (
  <div key={product._id}>
    <p>{product.product_name}</p>
    <p>{product.product_icon}</p>
    <p>{product.product_description}</p>
  </div>
))} */}
        <Row>

          <Col lg="4" md="6" sm="6">
            <Card className="card-stats">
              <CardBody>
                <Row>
                  <Col md="12" xs="12">
                    <div className="icon-big text-center icon-warning">
                      <i style={{fontSize:'24px'}} className="nc-icon nc-credit-card text-warning" />
                    </div>
                  </Col>
                  <Col md="12" xs="12">
                    <div >
                      <h6 align="center">Card</h6>
                      <CardTitle style={{margin:'-0.5em 1em'}} tag="p">150 </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
              <CardFooter>
                <hr />
                <div className="stats">
                  Total Registered Card
                </div>
              </CardFooter>
            </Card>
          </Col>

        </Row>
      </div>
    </>
  );
}

export default Products;
