// SignupComponent.js
import React, { useState } from 'react';
import { Modal, ModalHeader, ModalBody, Form, FormGroup, Label, Input, Button, NavLink } from 'reactstrap';

const SignupComponent = ({ isOpen, toggle, onSignup, toggleLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignup = () => {
    // Implement your signup logic here
    console.log('Signing up with:', email, password);
    // Call the onSignup callback or any other logic you want after successful signup
    onSignup();
  };

  return (
    <Modal isOpen={isOpen} toggle={toggle} 
    style={{
      maxWidth:'500px', 
      padding:'20px', 
      margin:'0 auto',
      backgroundColor:'white',
      boxShadow:'0 0 10px rgba(0, 0, 0, 0.1);'
    }}
      >
      <ModalHeader toggle={toggle}>Signup</ModalHeader>
      <ModalBody>
        <Form>
          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" id="email" placeholder="Enter your email" onChange={(e) => setEmail(e.target.value)} />
          </FormGroup>
          <FormGroup>
            <Label for="password">Password</Label>
            <Input type="password" id="password" placeholder="Enter your password" onChange={(e) => setPassword(e.target.value)} />
          </FormGroup>
          <Button color="primary" onClick={handleSignup}>
            Signup
          </Button>
          <p className="mt-2">
            Already have an account?{' '}
            <NavLink href="#" onClick={toggleLogin}>
              Login here
            </NavLink>
          </p>
        </Form>
      </ModalBody>
    </Modal>
  );
};

export default SignupComponent;
