import React from 'react'
import { Route, Navigate } from 'react-router-dom'

const ProtectedRoute = ({ role, path, element }) => {
    // Assume you have a function to get the user's role from your authentication context or state
    const userRole = getUserRole();
  
    if (userRole !== role) {
      // If the user's role doesn't match, redirect to a default route
      return <Navigate to="/" />;
    }
  
    // If the user's role matches, render the protected route
    return <Route path={path} element={element} />;
  };
  
  export default ProtectedRoute;
