import React, {useState, useEffect} from "react";
// react plugin used to create charts
// reactstrap components
import {
  Card,
  CardBody,
  CardFooter,
  CardTitle,
  Row,
  Col,
} from "reactstrap";
import '../assets/css/style.css';
import { Link } from "react-router-dom";  
import axios from "axios";

function Products() {
  const [products, setProducts] = useState([]);
  const fetchProducts = () => {
    axios.get('http://localhost:3001/admin/products/')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  };
  
  useEffect(() => {
    fetchProducts();
  }, []);
  return (
    <>
     <div className="content">
  <Row>
    <Col lg="3" md="6" sm="6" style={{ margin: '0em auto 2em 0em' }}>
      <Link to='add-product/'>
        <button className="add-product">Add A Product</button>
      </Link>
    </Col>
  </Row>
  <Row>
    {products.map(product => (
      <Col lg="4" md="6" sm="6" key={product._id}>
        <Card className="card-stats">
          <CardBody>
            <Row>
              <Col md="4" xs="12">
                <Card>
                  <CardBody>
                    <div className="icon-big text-center icon-warning">
                      <i style={{ fontSize: '68px' }} className="nc-icon nc-credit-card text-warning" />
                    </div>
                    <div>
                      <h6 align="center">{product.product_name}</h6>
                      {/* Add more details as needed */}
                    </div>
                  </CardBody>
                  <CardFooter>
                    <hr />
                    <div className="stats">
                      {product.product_description}
                    </div>
                  </CardFooter>
                </Card>
              </Col>
            </Row>
          </CardBody>
        </Card>
      </Col>
    ))}
  </Row>
</div>

    </>
  );
}

export default Products;
