/* eslint-disable react-hooks/rules-of-hooks */
import React, { useState, useEffect } from "react";
import {
    Button,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    FormGroup,
    Form,
    Input,
    Row,
    Col,
} from "reactstrap";
import {  useNavigate } from "react-router-dom";
import axios from "axios";
import jwt from 'jsonwebtoken';


function Support(props) {
    const [email, setEmail] = useState();
    const [ticket, setTicket] = useState();
    const [description, setDescription] = useState();

    const navigate = useNavigate();
    const getCurrentDateTime = () => {
        const currentDateTime = new Date().toISOString();
        return currentDateTime;
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        // console.log('Support Data:', supportData); 

        axios.post('http://localhost:3001/user/support',
        { email:localStorage.getItem('uid'), ticket, description, datetime:getCurrentDateTime()   })
            .then(res => {
                alert('Support Made');
                navigate('/user/dashboard');
            })
            .catch(err => console.log(err));
    };

    useEffect(() => {
        const storedEmail = localStorage.getItem('uid');
        console.log('Stored email:', storedEmail);
        if (storedEmail) {
            setEmail('storedEmail');
        }
    }, []);
    

    return (
        <>
            <div className="content">
               
                    <Row>
                        <Col md="8" style={{ margin: 'auto' }}>
                            <Card className="card-user">
                                <CardHeader>
                                    <CardTitle tag="h5">Submit Support Ticket</CardTitle>
                                </CardHeader>
                                <CardBody>
                                    <Form onSubmit={handleSubmit}>
                                        <Row>
                                            <Col md="12">
                                                <FormGroup>
                                                    <label>Ticket Title</label>
                                                    <Input
                                                        type="text"
                                                        placeholder=""
                                                        onChange={(e) => setTicket(e.target.value)}
                                                    />
                                                </FormGroup>
                                            </Col>
                                        </Row>
                                        <Row>
                                            <Col md="12">
                                                <FormGroup>
                                                    <label>Ticket Description</label>
                                                    <Input
                                                        type="textarea"
                                                        placeholder=""
                                                        onChange={(e) => setDescription(e.target.value)}
                                                    />
                                                </FormGroup>
                                            </Col>
                                        </Row>
                                        <Row>
                                            <div className="update ml-auto mr-auto">
                                                <Button
                                                    className="btn-round"
                                                    color="primary"
                                                    type="submit"
                                                >
                                                    Submit Ticket
                                                </Button>
                                            </div>
                                        </Row>
                                    </Form>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </div>
        </>
    );
}

export default Support;
