// LoginComponent.js
import React, { useState } from 'react';
import { Modal, ModalHeader, ModalBody, Form, FormGroup, Label, Input, Button, NavLink } from 'reactstrap';

const LoginComponent = ({ isOpen, toggle, onLogin, toggleSignup }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Implement your login logic here
    console.log('Logging in with:', email, password);
    // Call the onLogin callback or any other logic you want after successful login
    onLogin();
  };

  return (
    <Modal isOpen={isOpen} toggle={toggle} style={{position:'fixed', top:'50%', left:'50%', transform:'translate={(-50%, -50%)'}}>
      <ModalHeader toggle={toggle}>Login</ModalHeader>
      <ModalBody>
        <Form>
          <FormGroup>
            <Label for="email">Email</Label>
            <Input type="email" id="email" placeholder="Enter your email" onChange={(e) => setEmail(e.target.value)} />
          </FormGroup>
          <FormGroup>
            <Label for="password">Password</Label>
            <Input type="password" id="password" placeholder="Enter your password" onChange={(e) => setPassword(e.target.value)} />
          </FormGroup>
          <Button color="primary" onClick={handleLogin}>
            Login
          </Button>
          <p className="mt-2">
            Don't have an account?{' '}
            <NavLink href="#" onClick={toggleSignup}>
              Signup here
            </NavLink>
          </p>
        </Form>
      </ModalBody>
    </Modal>
  );
};

export default LoginComponent;
