import React
 ,{useState,
  useEffect} 
from 'react';
// reactstrap components
import {
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    Table,
    Row,
    Col,
  } from "reactstrap";
import {
     Link
    //, useNavigate 
} 
    from "react-router-dom";
import axios from 'axios';

function Admins() {
    // eslint-disable-next-line
  // const [sucess,setSucess] =useState()
  // const navigate = useNavigate();
const [admins,setAdmins] =useState([])
  axios.defaults.withCredentials = true;
  useEffect(() => {
    axios.get('http://localhost:3001/super-admin/admins')
      .then(admins => setAdmins(admins.data))
      .catch(err=> console.log(err))
  }, []);
  const handleDelete = (userId) => {
    axios.delete(`http://localhost:3001/super-admin/admins/${userId}`)
      .then(response => {
        console.log(response.data.message);
        // Refresh the admin list after deletion
        fetchAdmins();
      })
      .catch(error => console.error(error));
  };
  
  const fetchAdmins = () => {
    axios.get('http://localhost:3001/super-admin/admins')
      .then(admins => setAdmins(admins.data))
      .catch(err => console.log(err));
  };
  
  useEffect(() => {
    fetchAdmins();
  }, []);
    const scrollbarStyles = {
        WebkitOverflowScrolling: 'touch', // Optional: Improve scrolling smoothness      
      };
  return (
   <>
    <div className="content">
        <Row>
          <Col md="12">
            <Card>
              <CardHeader>
                <CardTitle tag="h4">Admins Operating</CardTitle>
              </CardHeader>
              <CardBody>
                <Table style={scrollbarStyles} responsive>
                  <thead className='tableHead'>
                    <tr>
                      <th>Admin ID</th>
                      <th>Admin Name</th>
                      <th>Acess</th>
                      <th className="">Operation</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      admins.map(user=>{
                       return <tr>
                          <td>{user._id}</td>
                          <td>{user.firstname} &nbsp; {user.lastname}</td>
                          <td>All</td>
                          <td style={{display:'flex'}}>
                            <Link to="/super-admin/admins/edit" className="nav-link btn-rotate">
                              <i style={{fontSize:20}} class="fa-solid fa-pen-to-square text-secondary edeButton"></i>
                            </Link>
                              <span style={{fontSize:22}}> || </span>
                            <button onClick={() => handleDelete(user._id)} to="/super-admin/admins/delete" className="nav-link btn-rotate">
                                <i style={{fontSize:20}} class="fa-solid fa-trash text-danger edeButton"></i> 
                            </button>
                            </td>
                        </tr>
                      })
                    }
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
      {/*  */}
   </>
  )
}

export default Admins;
