import React, { useState, useEffect }  from "react";
import ReactDOM from 'react-dom';

import { BrowserRouter, 
  Route, 
  Routes, 
 Navigate 
} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.css";
import "assets/scss/dashboard.scss?v=1.3.0";
import "assets/demo/demo.css";
import "perfect-scrollbar/css/perfect-scrollbar.css";
import MyProfile from "views/Profile"; 
import AdminLayout from "layouts/Admin.js";
import ViewCard from "views/viewCard";
import EditUser from "views/editUser";
import EditOrder from "views/editOders";
import UserLayout from "layouts/UserDashboard";
import Home from "layouts/Home";
import LoginComponent from "views/Login";
import SignupComponent from "views/Signup";
import SuperAdmin from "layouts/SuperAdmin";
import ProtectedRoute from "components/ProtectedRoute";
import axios from 'axios';
const App = () => {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    // Fetch user role or authentication status from your API or context
    axios.get('http://localhost:3001/get-user-role')
      .then(res => {
        setUserRole(res.data.role);
      })
      .catch(err => {
        console.error(err);
      });
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />

        <Route path="/login" element={<LoginComponent />} />
        <Route path="/signup" element={<SignupComponent />} />

        <ProtectedRoute role="super-admin" userRole={userRole} path="/super-admin/" element={<SuperAdmin />} />

        <ProtectedRoute role="admin" userRole={userRole} path="/admin/*" element={<AdminLayout />} />
        <ProtectedRoute role="admin" userRole={userRole} path="/admin/" element={<Navigate to="/admin/dashboard" replace />} />
        <ProtectedRoute role="admin" userRole={userRole} path="/admin/my-profile" element={<MyProfile />} />
        <ProtectedRoute role="admin" userRole={userRole} path="/admin/cards/view-card" element={<ViewCard />} />

        <ProtectedRoute role="user" userRole={userRole} path="/user/*" element={<UserLayout />} />
        <ProtectedRoute role="user" userRole={userRole} path="/user/" element={<Navigate to="/user/dashboard" replace />} />
        <ProtectedRoute role="user" userRole={userRole} path="/admin/users/edit-user" element={<EditUser />} />
        <ProtectedRoute role="user" userRole={userRole} path="/admin/users/edit-order" element={<EditOrder />} />
      </Routes>
    </BrowserRouter>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
